import sys

def __init_plugin__(self=None):
    from pymol import plugins
    plugins.addmenuitem('APBS Tools2.1 (placeholder)', lambda: dialog(self))

def dialog(self):
    if sys.version_info[0] < 3:
        import tkMessageBox
    else:
        from tkinter import messagebox as tkMessageBox

    tkMessageBox.showinfo('Info',
            'We recommend to use the new "APBS Electrostatics" plugin.\n'
            'If you want to use the old APBS Tools plugin, then '
            'please download apbsplugin.py from\n'
            'https://pymolwiki.org/index.php/apbsplugin\n'
            'and install it with the Plugin Manager.',
            parent=self.root)
